chat_template:
{% set ns = namespace(found=false, is_first=false, is_tool=false) %}
{%- for message in messages %}
  {%- if message['role'] == 'system' %}
    {{ '<｜begin of sentence｜><｜system｜>' + message['content'] + '<｜end of sentence｜>' }}
  {%- elif message['role'] == 'user' %}
    {%- if message.get('content') and message.get('tool_results') %}
      {{ '<｜begin of sentence｜><｜User｜>' + message['content'] }}
      {%- for result in message['tool_results'] %}
        {{ '
<｜tool result begin｜>' + result['name'] + '
```json
' + result['result'] + '
```<｜tool result end｜>' }}
      {%- endfor %}
      {{ '<｜end of sentence｜>' }}
    {%- elif message.get('content') %}
      {{ '<｜begin of sentence｜><｜User｜>' + message['content'] + '<｜end of sentence｜>' }}
    {%- endif %}
  {%- elif message['role'] == 'assistant' %}
    {%- if message.get('content') is none or message.get('content') == '' %}
      {%- set ns.is_tool = false -%}
      {%- for tool in message.get('tool_calls', []) %}
        {%- if not ns.is_first %}
          {{ '<｜Assistant｜><｜tool calls begin｜><｜tool call begin｜>' + tool['type'] + '<｜tool sep｜>' + tool['function']['name'] + '
```json
' + (tool['function']['arguments']|tojson) + '
```<｜tool call end｜>' }}
          {%- set ns.is_first = true -%}
        {%- else %}
          {{ '
<｜tool call begin｜>' + tool['type'] + '<｜tool sep｜>' + tool['function']['name'] + '
```json
' + (tool['function']['arguments']|tojson) + '
```<｜tool call end｜>' }}
          {{ '<｜tool calls end｜><｜end of sentence｜>' }}
        {%- endif %}
      {%- endfor %}
    {%- else %}
      {{ '<｜begin of sentence｜><｜Assistant｜>' + message['content'] + '<｜end of sentence｜>' }}
    {%- endif %}
  {%- endif %}
{%- endfor %}
{%- if add_generation_prompt %}
  {{ '<｜begin of sentence｜><｜Assistant｜>' }}
{%- endif %}
